mapper.update:
update comment set
    content=#{content}
where id=#{id}

mapper.select:
select id, member_id, movie_id, content, indate, good, bad
from comment
where member_id=#{memberId}

select id, member_id, movie_id, content, indate, good, bad
from comment
where id=#{id}

mapper.update:
update comment set
    good=((select tmp_table.nowgood from (select good nowgood from comment where id=#{id}) tmp_table)+1)
where id=#{id};

update comment set
    bad=((select tmp_table.nowbad from (select bad nowbad from comment where id=#{id}) tmp_table)+1)
where id=#{id};
